package Basicjava;

public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

         int j=1;
		
		//1.For loop
		//2.while loop
		//3.Do while loop
		
		// 1-10;
		
		
		
    //1.for loop
		
		for (int i=1;i<=10;i++)
		{
			System.out.println(i);
		}
		
		
		System.out.println("***********");
	//2.While loop
		
	while(j<=10)
		{
			System.out.println(j);
			j++;
		}
		
	//3.Do while loop
		
		do {
			System.out.println(j);
			j++;
		}
		while(j<=10);
	}

}
